SELECT DISTINCT "PORECLINE"."REC_NUMBER", "PORECLINE"."LINE_NBR", "PORECLINE"."ITEM", "PORECLINE"."DESCRIPTION", "PORECLINE"."ENT_REC_QTY", "PORECLINE"."ENT_REC_UOM", "POLINE"."LINE_NBR", "PORECLINE"."REC_DATE", "POLINE"."LOCATION", "POLINE"."PO_NUMBER", "POLINE"."VENDOR" FROM   "Prod"."lawson"."POLINE" "POLINE" INNER JOIN "Prod"."lawson"."PORECLINE" "PORECLINE" ON ((("POLINE"."COMPANY"="PORECLINE"."COMPANY") AND ("POLINE"."ITEM"="PORECLINE"."ITEM")) AND ("POLINE"."PO_NUMBER"="PORECLINE"."PO_NUMBER")) AND ("POLINE"."LINE_NBR"="PORECLINE"."PO_LINE_NBR") WHERE  "PORECLINE"."ENT_REC_UOM"<>'LB' AND ("PORECLINE"."REC_DATE">={ts '2016-03-31 00:00:00'} AND "PORECLINE"."REC_DATE"<{ts '2016-04-01 00:00:00'}) AND "POLINE"."LOCATION"='1' ORDER BY "PORECLINE"."REC_NUMBER", "POLINE"."LINE_NBR"